mapper.update:
DECLARE
	max_payment_id VARCHAR2(8);
BEGIN            
	SELECT MAX(SUBSTR(payment_id, 1, 8))
	INTO max_payment_id
	FROM payment;

	IF
		TO_CHAR(SYSDATE, 'YYYYMMDD') != max_payment_id
	THEN
		EXECUTE IMMEDIATE 'DROP SEQUENCE payment_id_seq';
		EXECUTE IMMEDIATE 'CREATE SEQUENCE payment_id_seq';
	END IF;
END;
